import React, { ComponentType } from "react";

interface Props {
  className: string;
  pill: string;
}

const EinkSubwayStatusPill: ComponentType<Props> = ({ className, pill }) => {
  switch (pill) {
    case "blue-line":
      return blueLinePill(className);
    case "orange-line":
      return orangeLinePill(className);
    case "red-line":
      return redLinePill(className);
    case "green-line":
      return greenLinePill(className);
    case "gl-b":
      return glbPill(className);
    case "gl-c":
      return glcPill(className);
    case "gl-d":
      return gldPill(className);
    case "gl-e":
      return glePill(className);
    case "green-b-circle":
      return greenBCircle(className);
    case "green-c-circle":
      return greenCCircle(className);
    case "green-d-circle":
      return greenDCircle(className);
    case "green-e-circle":
      return greenECircle(className);
    default:
      return null;
  }
};

